(595, 1, '2021-06-10 13:52:01', '2021-06-10 16:52:01', '', 'Pesca-Esportiva-em-Ubatuba-Litoral-Norte-do-estado-de-Sao-Paulo-240x135', '', 'inherit', 'open', 'closed', '', 'pesca-esportiva-em-ubatuba-litoral-norte-do-estado-de-sao-paulo-240x135', '', '', '2021-06-10 13:52:01', '2021-06-10 16:52:01', '', 461, 'http://navegarmais.com.br/site/wp-content/uploads/2019/08/Pesca-Esportiva-em-Ubatuba-Litoral-Norte-do-estado-de-Sao-Paulo-240x135.jpg', 0, 'attachment', 'image/jpeg', 0);
